import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  money: 1000,
  health: 100,
  inventory: {
    weed: 0,
    cocaine: 0,
    heroin: 0,
    meth: 0,
    ecstasy: 0
  },
  level: 0,
  reputation: 0,
  currentLocation: 'manhattan'
};

export const playerSlice = createSlice({
  name: 'player',
  initialState,
  reducers: {
    updateMoney: (state, action) => {
      state.money += action.payload;
    },
    updateHealth: (state, action) => {
      state.health = Math.max(0, Math.min(100, state.health + action.payload));
    },
    updateInventory: (state, action) => {
      const { drug, amount } = action.payload;
      state.inventory[drug] += amount;
    },
    updateLocation: (state, action) => {
      state.currentLocation = action.payload;
    },
    levelUp: (state) => {
      state.level += 1;
    },
    updateReputation: (state, action) => {
      state.reputation += action.payload;
    },
    resetPlayer: () => initialState,
    loadPlayerState: (state, action) => {
      return { ...action.payload };
    }
  }
});

export const { 
  updateMoney, 
  updateHealth, 
  updateInventory, 
  updateLocation, 
  levelUp, 
  updateReputation,
  resetPlayer,
  loadPlayerState
} = playerSlice.actions;

export default playerSlice.reducer;
