import React from 'react';
import { useSelector } from 'react-redux';
import styled from 'styled-components';

const StatsContainer = styled.div`
  background: rgba(20, 20, 20, 0.8);
  border-radius: 8px;
  padding: 1rem;
  border: 1px solid rgba(255, 255, 255, 0.1);
  height: 380px;
  overflow-y: auto;
  display: flex;
  flex-direction: column;
`;

const StatTitle = styled.h3`
  color: var(--color-primary);
  margin-bottom: 0.5rem;
  border-bottom: 1px solid rgba(255, 77, 0, 0.3);
  padding-bottom: 0.5rem;
  font-size: 1.1rem;
`;

const StatRow = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 0.5rem;
  padding: 0.2rem 0;
`;

const StatLabel = styled.span`
  color: rgba(255, 255, 255, 0.7);
  font-size: 0.95rem;
`;

const StatValue = styled.span`
  font-weight: bold;
  color: ${props => props.color || 'white'};
  font-size: 0.95rem;
`;

const HealthBar = styled.div`
  width: 100%;
  height: 10px;
  background: rgba(0, 0, 0, 0.3);
  border-radius: 5px;
  margin-top: 5px;
  overflow: hidden;
`;

const HealthFill = styled.div`
  height: 100%;
  width: ${props => props.health}%;
  background: ${props => {
    if (props.health > 70) return 'var(--color-success)';
    if (props.health > 30) return 'var(--color-warning)';
    return 'var(--color-danger)';
  }};
  transition: width 0.3s ease;
`;

const InventoryList = styled.div`
  margin-top: 1rem;
`;

const InventoryItem = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 0.2rem 0;
  border-bottom: 1px dotted rgba(255, 255, 255, 0.1);
  
  &:last-child {
    border-bottom: none;
  }
`;

const PlayerStats = () => {
  const { money, health, inventory, level, reputation, currentLocation } = useSelector(state => state.player);
  const { day } = useSelector(state => state.game);
  
  // Format location name for display
  const formatLocation = (location) => {
    return location.charAt(0).toUpperCase() + location.slice(1).replace(/([A-Z])/g, ' $1');
  };
  
  return (
    <StatsContainer>
      <StatTitle>Player Stats</StatTitle>
      
      <StatRow>
        <StatLabel>Level:</StatLabel>
        <StatValue>{level}</StatValue>
      </StatRow>
      
      <StatRow>
        <StatLabel>Money:</StatLabel>
        <StatValue color="#00cc66">${money.toLocaleString()}</StatValue>
      </StatRow>
      
      <StatRow>
        <StatLabel>Health:</StatLabel>
        <StatValue>{health}%</StatValue>
      </StatRow>
      
      <HealthBar>
        <HealthFill health={health} />
      </HealthBar>
      
      <StatRow>
        <StatLabel>Reputation:</StatLabel>
        <StatValue>{reputation}</StatValue>
      </StatRow>
      
      <StatRow>
        <StatLabel>Day:</StatLabel>
        <StatValue>{day}</StatValue>
      </StatRow>
      
      <StatRow>
        <StatLabel>Location:</StatLabel>
        <StatValue color="var(--color-secondary)">{formatLocation(currentLocation)}</StatValue>
      </StatRow>
      
      <StatTitle>Inventory</StatTitle>
      <InventoryList>
        {Object.entries(inventory).map(([drug, amount]) => (
          <InventoryItem key={drug}>
            <StatLabel>{drug.charAt(0).toUpperCase() + drug.slice(1)}</StatLabel>
            <StatValue>{amount} units</StatValue>
          </InventoryItem>
        ))}
      </InventoryList>
    </StatsContainer>
  );
};

export default PlayerStats;
