import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import styled from 'styled-components';

// Components
import PlayerStats from './PlayerStats';
import LocationMap from './LocationMap';
import DrugMarket from './DrugMarket';
import QuestLog from './QuestLog';
import GameLog from './GameLog';

// Utils
import { autoSaveGame } from '../utils/localStorage';
import { resetGame } from '../store/gameSlice';

const GameGrid = styled.div`
  display: grid;
  grid-template-columns: 300px 1fr;
  grid-template-rows: auto 1fr auto;
  gap: 1rem;
  height: calc(100vh - 150px);
  max-width: 1200px;
  margin: 0 auto;
`;

const LeftColumn = styled.div`
  grid-column: 1;
  grid-row: 1 / span 3;
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const MainContent = styled.div`
  grid-column: 2;
  grid-row: 1 / span 2;
  display: flex;
  flex-direction: column;
  gap: 1rem;
  overflow: hidden;
`;

const BottomRow = styled.div`
  grid-column: 2;
  grid-row: 3;
  margin-top: 1rem;
`;

const HeaderRow = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1rem;
`;

const GameTitle = styled.h2`
  color: var(--color-primary);
  margin: 0;
`;

const BackButton = styled.button`
  background-color: var(--color-secondary);
  padding: 0.5rem 1rem;
  font-size: 0.9rem;
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const GameContainer = () => {
  const dispatch = useDispatch();
  const gameState = useSelector(state => state);
  
  // Auto-save on any state change
  useEffect(() => {
    const saveTimer = setTimeout(() => {
      autoSaveGame(gameState);
    }, 1000);
    
    return () => clearTimeout(saveTimer);
  }, [gameState]);
  
  const handleBackToMain = () => {
    // Auto-save before going back to ensure game state is saved
    autoSaveGame(gameState);
    dispatch(resetGame());
  };
  
  return (
    <>
      <HeaderRow>
        <GameTitle>Drug Wars</GameTitle>
        <BackButton onClick={handleBackToMain}>
          ‚Üê Back to Main Menu
        </BackButton>
      </HeaderRow>
      
      <GameGrid>
        <LeftColumn>
          <PlayerStats />
          <QuestLog />
        </LeftColumn>
        
        <MainContent>
          <LocationMap />
          <DrugMarket />
        </MainContent>
        
        <BottomRow>
          <GameLog />
        </BottomRow>
      </GameGrid>
    </>
  );
};

export default GameContainer;
